{
  "name": "data_processing_validator",
  "parameters": {
    "data": {},
    "schema": {
      "$schema": "http://json-schema.org/draft-07/schema#",
      "type": "object",
      "properties": {
        "id": {"type": "string"},
        "value": {"type": "number"}
      },
      "required": ["id"]
